INSERT INTO `lk_config` VALUES ('22', 'music_description3', '音高视唱练习为主题，引入复合型节奏练习及切分，让孩子建立在自然音阶基础上保持稳定的演唱状态，具备基础的合唱能力，同时提升有难度的奥尔夫乐器演奏教学；', '乐开音乐描述三', 'textarea', '');
