INSERT INTO `lk_photo` VALUES ('10', '青春修炼手册', '哇哦，最后一首嗨翻全场的歌曲来了，大家知道什么音乐吗？果然被你们猜中了，由这位小朋友领唱的《青春修炼手册》，带动了全场一起演唱，将整个整个音乐party带到了最高潮', '2017-09-25 22:07:15', '0', './upload/2017-09-25/20170925215756_983.jpg');
