INSERT INTO `wp_options` VALUES ('148', 'option_tree_settings', 'a:3:{s:15:\"contextual_help\";a:1:{s:7:\"content\";a:1:{i:0;a:3:{s:2:\"id\";s:12:\"general_help\";s:5:\"title\";s:13:\"Documentation\";s:7:\"content\";s:1105:\"\r\n			<h1>Hueman</h1>\r\n			<p>Thanks for using this theme! Enjoy.</p>\r\n			<ul>\r\n				<li>Read the theme documentation <a target=\"_blank\" href=\"http://qxu1002120312.my3w.com/wp-content/themes/hueman/functions/documentation/documentation.html\">here</a></li>\r\n				<li>Download the sample child theme <a href=\"https://github.com/AlxMedia/hueman-child/archive/master.zip\">here</a></li>\r\n				<li>Download or contribute translations <a target=\"_blank\" href=\"https://github.com/AlxMedia/hueman-languages\">here</a></li>\r\n				<li>Feel free to rate/review the theme <a target=\"_blank\" href=\"http://wordpress.org/support/view/theme-reviews/hueman\">here</a></li>\r\n				<li>If you have theme questions, go <a target=\"_blank\" href=\"http://wordpress.org/support/theme/hueman\">here</a></li>\r\n				<li>Hueman is on <a target=\"_blank\" href=\"https://github.com/AlxMedia/hueman\">GitHub</a></li>\r\n			</ul>\r\n			<hr />\r\n			<p>You can support the theme author by donating <a target=\"_blank\" href=\"https://www.paypal.com/cgi-bin/webscr?cmd=_s-xclick&hosted_button_id=K54RW72RZM2HN\">here</a> – any amount is always appreciated.</p>\r\n		\";}}}s:8:\"sections\";a:8:{i:0;a:2:{s:2:\"id\";s:7:\"general\";s:5:\"title\";s:7:\"General\";}i:1;a:2:{s:2:\"id\";s:4:\"blog\";s:5:\"title\";s:4:\"Blog\";}i:2;a:2:{s:2:\"id\";s:6:\"header\";s:5:\"title\";s:6:\"Header\";}i:3;a:2:{s:2:\"id\";s:6:\"footer\";s:5:\"title\";s:6:\"Footer\";}i:4;a:2:{s:2:\"id\";s:6:\"layout\";s:5:\"title\";s:6:\"Layout\";}i:5;a:2:{s:2:\"id\";s:8:\"sidebars\";s:5:\"title\";s:8:\"Sidebars\";}i:6;a:2:{s:2:\"id\";s:12:\"social-links\";s:5:\"title\";s:12:\"Social Links\";}i:7;a:2:{s:2:\"id\";s:7:\"styling\";s:5:\"title\";s:7:\"Styling\";}}s:8:\"settings\";a:68:{i:0;a:6:{s:2:\"id\";s:6:\"custom\";s:5:\"label\";s:17:\"Custom Stylesheet\";s:4:\"desc\";s:285:\"Load custom stylesheet [ <strong>custom.css</strong> ]<br /><i>Note: You must backup this file before a theme update. Consider using a <a target=\"_blank\" href=\"http://codex.wordpress.org/Child_Themes\">child theme</a> instead. A sample child theme is available in the help dropdown.</i>\";s:3:\"std\";s:3:\"off\";s:4:\"type\";s:6:\"on-off\";s:7:\"section\";s:7:\"general\";}i:1;a:6:{s:2:\"id\";s:10:\"responsive\";s:5:\"label\";s:17:\"Responsive Layout\";s:4:\"desc\";s:67:\"Mobile and tablet optimizations [ <strong>responsive.css</strong> ]\";s:3:\"std\";s:2:\"on\";s:4:\"type\";s:6:\"on-off\";s:7:\"section\";s:7:\"general\";}i:2;a:6:{s:2:\"id\";s:11:\"sidebar-top\";s:5:\"label\";s:17:\"Sidebar Top Boxes\";s:4:\"desc\";s:32:\"Boxes at the top of the sidebars\";s:3:\"std\";s:2:\"on\";s:4:\"type\";s:6:\"on-off\";s:7:\"section\";s:7:\"general\";}i:3;a:7:{s:2:\"id\";s:19:\"mobile-sidebar-hide\";s:5:\"label\";s:22:\"Mobile Sidebar Content\";s:4:\"desc\";s:61:\"Hide sidebar content on low-resolution mobile devices (320px)\";s:4:\"type\";s:5:\"radio\";s:3:\"std\";s:1:\"1\";s:7:\"section\";s:7:\"general\";s:7:\"choices\";a:4:{i:0;a:2:{s:5:\"value\";s:1:\"1\";s:5:\"label\";s:13:\"Show sidebars\";}i:1;a:2:{s:5:\"value\";s:2:\"s1\";s:5:\"label\";s:20:\"Hide primary sidebar\";}i:2;a:2:{s:5:\"value\";s:2:\"s2\";s:5:\"label\";s:22:\"Hide secondary sidebar\";}i:3;a:2:{s:5:\"value\";s:5:\"s1-s2\";s:5:\"label\";s:18:\"Hide both sidebars\";}}}i:4;a:5:{s:2:\"id\";s:7:\"favicon\";s:5:\"label\";s:7:\"Favicon\";s:4:\"desc\";s:56:\"Upload a 16x16px Png/Gif image that will be your favicon\";s:4:\"type\";s:6:\"upload\";s:7:\"section\";s:7:\"general\";}i:5;a:5:{s:2:\"id\";s:8:\"rss-feed\";s:5:\"label\";s:14:\"FeedBurner URL\";s:4:\"desc\";s:173:\"Enter your full FeedBurner URL (or any other preferred feed URL) if you wish to use FeedBurner over the standard WordPress feed e.g. http://feeds.feedburner.com/yoururlhere \";s:4:\"ty